@QuarkusIntegrationTest
public class PetResourceIT {
	private static final int NUM_RECORDS = 3;

	@Test
	public void getAll() {
		var pets = get("/pets")
			.then()
			.contentType(ContentType.JSON)
			.statusCode(OK.getStatusCode())
			.extract()
			.body()
			.jsonPath().getList(".", Pet.class);

		assertThat(pets)
			.isNotNull()
			.hasSize(NUM_RECORDS)
			.extracting(Pet::getName, Pet::getKind)
			.containsExactlyInAnyOrder(
				tuple("fluffy", "cat"),
				tuple("herman", "dog"),
				tuple("cutiepie", "monkey")
			);
	}

	@Test
	public void getByKind() {
		get("/pets?kind={kind}", "cat")
			.then()
			.contentType(ContentType.JSON)
			.statusCode(OK.getStatusCode())
			.body("$.size()", is(1))
			.body("[0].name", is("fluffy"))
			.body("[0].kind", is("cat"));
	}

	@Test
	public void getByIdFound() {
		get("/pets/{id}", 1L)
			.then()
			.contentType(ContentType.JSON)
			.statusCode(OK.getStatusCode())
			.body("name", is("fluffy"))
			.body("kind", is("cat"));
	}

	@Test
	public void getByIdNotFound() {
		get("/pets/{id}", NUM_RECORDS + 1)
			.then()
			.statusCode(NOT_FOUND.getStatusCode())
			.body(blankOrNullString());
	}
}

@Test
public void getByKind() {
	// Set up mock to return a pet when repo.findPetsByKind("cat") is called
	when(this.petRepository.findPetsByKind("cat"))
		.thenReturn(List.of(new Pet(1L, "fluffy", "cat", "Eric")));

	// Execute GET to /pets?kind=cat & assert
	get("/pets?kind={kind}", "cat").then()
		.contentType(ContentType.JSON)
		.statusCode(OK.getStatusCode())
		.body("$.size()", is(1))
		.body("[0].name", is("fluffy"))
		.body("[0].kind", is("cat"))
		.body("[0].adoptedBy", is("Eric"));

	// Verify interactions
	verify(this.petRepository).findPetsByKind("cat");
	verifyNoMoreInteractions(this.petRepository);
}

@Test
public void getByIdFound() {
	// Set up mock to return a pet when repo.findByIdOptional(1) is called
	when(this.petRepository.findByIdOptional(1L))
		.thenReturn(Optional.of(new Pet(1L, "fluffy", "cat", "Eric")));

	// Execute GET to /pets/1 & assert OK
	get("/pets/{id}", 1L).then()
		.contentType(ContentType.JSON)
		.statusCode(OK.getStatusCode())
		.body("name", is("fluffy"))
		.body("kind", is("cat"))
		.body("adoptedBy", is("Eric"));

	// Verify interactions
	verify(this.petRepository).findByIdOptional(1L);
	verifyNoMoreInteractions(this.petRepository);
}

@Test
public void getByIdNotFound() {
	// Set up mock to return empty when repo.findByIdOptional(1) is called
	when(this.petRepository.findByIdOptional(1L))
		.thenReturn(Optional.empty());

	// Execute GET to /pets/1 & assert NOT_FOUND
	get("/pets/{id}", 1L).then()
		.statusCode(NOT_FOUND.getStatusCode())
		.body(blankOrNullString());

	// Verify interactions
	verify(this.petRepository).findByIdOptional(1L);
	verifyNoMoreInteractions(this.petRepository);
}
